import org.codehaus.jettison.json.JSONObject;
import org.codehaus.jettison.json.JSONArray;
import java.sql.Types;
import java.sql.ResultSet;
import java.sql.ResultSetMetaData;

public class ToJSON{
    public static JSONArray toJsonArray(ResultSet resultset){
        JSONArray json=new JSONArray();
        try{
            ResultSetMetaData md = resultset.getMetaData();
            while(resultset.next()){
                int columnas= md.getColumnCount();
                JSONObject obj=new JSONObject();
                for(int i=1;i < columnas;i++){
                    String nombre = md.getColumnName(i);

                    if(md.getColumnType(i)==Types.INTEGER){
                        obj.put(nombre, resultset.getInt(nombre));
                    }else if(md.getColumnType(i)==Types.VARCHAR){
                        obj.put(nombre,resultset.getString(nombre));
                    }
                }
                json.put(obj);
            }
        }catch(Exception e){
            e.printStackTrace();
        }
        return json;
    }
