create_user:
CREATE USER '~username'@'%' IDENTIFIED BY '~username';
GRANT ALL PRIVILEGES ON *.* TO '~username'@'%'
WITH GRANT OPTION;

create_user_with_all_privileges:
CREATE USER '~1user'@'%' IDENTIFIED BY '~1user';
GRANT ALL PRIVILEGES ON ~2database.* TO '~1user'@'%'
WITH GRANT OPTION;

drop_foreign_key:
ALTER TABLE @1TABLE DROP FOREIGN KEY @2FOREIGNKEY;
ALTER TABLE @1TABLE DROP INDEX  @2FOREIGNKEY_IDX;
ALTER TABLE @1TABLE DROP COLUMN @3COLUMN;
